public class StrToInt {
    public int solution(String s) {

        int answer = Integer.parseInt(s);
//        System.out.println(answer);
        return answer;
    }

    public static void main(String[] args) {
        // TODO Auto-generated method stub


        String qwer = "-1234";
        StrToInt sol = new StrToInt();
        sol.solution(qwer);

    }
}
